import * as React from 'react';
import { useEffect, useState } from 'react';
import { DataGrid, GridColDef } from '@mui/x-data-grid';
import { Container, CssBaseline, Typography, Box } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Avatar from '@mui/material/Avatar';
import SchoolIcon from '@mui/icons-material/School';
import Button from '@mui/material/Button';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import { sha256 } from 'crypto-hash';

const defaultTheme = createTheme({
  palette: {
    primary: {
      main: '#ab0520', // primary color
    },
    secondary: {
      main: '#0c234b', // secondary color
    },
  },
});

interface Scholarship {
  id: number;
  scholarshipID: number;
  applications: number[];
  awardedApplications: number[];
  awardAmount: number;
  sponsorID: string;
  numberAvailable: number;
  majors: string;
  minors: string;
  gpa: number;
  deadline: Date;
  otherRequirements: string;
  scholarshipName: string;
}

const columns: GridColDef[] = [
  { field: 'scholarshipName', headerName: 'Scholarship Name', width: 200 },
  { field: 'awardAmount', headerName: 'Award Amount', width: 150 },
  { field: 'numberAvailable', headerName: 'Number Available', width: 180 },
  { field: 'deadline', headerName: 'Deadline', width: 150 },
];

export default function Scholarships() {
  const [rows, setRows] = useState<Scholarship[]>([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('http://localhost:8000/getallscholarships/');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        // Assuming the data structure is { scholarships: Scholarship[] }
        if (data && data.scholarships) {
          setRows(data.scholarships);
        } else {
          console.error('Invalid data structure in API response:', data);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }
    fetchData();
  }, []);


  return (
    <div style={{
      backgroundImage: 'url(/UofA1.jpeg)',
      backgroundSize: 'cover',
      height: '100vh',
    }}>
    <Box sx={{ display: 'flex' }}>
        <ThemeProvider theme={defaultTheme}>
            <CssBaseline />
              <Container component="main" maxWidth="lg"
                sx={
                  {
                    marginTop: 8,
                    flexDirection: 'column',
                    alignItems: 'center',
                    borderRadius: '10px',
                    backgroundColor: 'white',
                  }
                }
              >
                  <Box
                    sx={{
                      //marginTop: 8,
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      backgroundColor: 'white',
                      borderRadius: '10px',
                    }}
                  >
                    <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
                      <SchoolIcon />
                    </Avatar>
                    <Typography component="h1" variant="h5">
                      Scholarships
                    </Typography>
                    <div style={{ height: 400, width: '100%' }}>
                      <DataGrid
                        rows={rows}
                        columns={columns}
                        checkboxSelection
                      />
                    </div>
                  </Box>
              </Container>
        </ThemeProvider>
      </Box>
    </div>
  );
}
