import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Typography } from '@mui/material';

interface Scholarship {
    scholarshipID: string;
    applications: number;
    awardedApplications: number;
    awardAmount: number;
    sponsorID: string;
    numberAvailable: number;
    majors: string[];
    minors: string[];
    gpa: number;
    deadline: string;
    otherRequirements: string;
}

interface ScholarshipResponse {
    scholarship: Scholarship;
    error?: string;
}

interface ViewScholarshipProps {
    scholarshipId: string;
}

const ViewScholarship: React.FC<ViewScholarshipProps> = ({ scholarshipId }) => {
    const [scholarship, setScholarship] = useState<Scholarship | null>(null);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchScholarship = async () => {
            try {
                const response = await axios.get<ScholarshipResponse>(`http://localhost:8000/getscholarship/${scholarshipId}`);
                setScholarship(response.data.scholarship);
                setError(null);
            } catch (error: any) {
                setError(error.response.data.error as string);
            }
        };

        fetchScholarship();
    }, [scholarshipId]);

    if (error) {
        return <Typography variant="body1">{error}</Typography>;
    }

    if (!scholarship) {
        return <Typography variant="body1">Loading...</Typography>;
    }

    return (
        <div>
            <Typography variant="h1">Scholarship Details</Typography>
            <Typography variant="body1">Scholarship ID: {scholarship.scholarshipID}</Typography>
            <Typography variant="body1">Applications: {scholarship.applications}</Typography>
            <Typography variant="body1">Awarded Applications: {scholarship.awardedApplications}</Typography>
            <Typography variant="body1">Award Amount: {scholarship.awardAmount}</Typography>
            <Typography variant="body1">Sponsor ID: {scholarship.sponsorID}</Typography>
            <Typography variant="body1">Number Available: {scholarship.numberAvailable}</Typography>
            <Typography variant="body1">Majors: {scholarship.majors.join(', ')}</Typography>
            <Typography variant="body1">Minors: {scholarship.minors.join(', ')}</Typography>
            <Typography variant="body1">GPA: {scholarship.gpa}</Typography>
            <Typography variant="body1">Deadline: {scholarship.deadline}</Typography>
            <Typography variant="body1">Other Requirements: {scholarship.otherRequirements}</Typography>
        </div>
    );
};

export default ViewScholarship;
