import * as React from 'react';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import MuiTable from './mui-components/muitabelscholarships';    
import { useNavigate } from 'react-router-dom';

const defaultTheme = createTheme();

export default function Scholarships() {
  const navigate = useNavigate(); // Initialize the useNavigate hook

  // Function to handle scholarship selection and navigate to ViewScholarship page
  const handleScholarshipSelection = (scholarshipId: string) => {
    navigate(`/view-scholarship/${scholarshipId}`);
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      <Box sx={{ display: 'flex' }}>
        <CssBaseline />
        <Box style={{ width: '100%', height: '100vh' }}>
          <h1 style={{ textAlign: 'center' }}>Scholarships</h1>
          <Container style={{ display: "flex", minWidth: "90%", alignItems: "center" }}>

            <MuiTable endpoint="http://localhost:8000/getallscholarships/" />
          </Container>
        </Box>
      </Box>
    </ThemeProvider>
  );
}
