import * as React from 'react';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import { useHistory } from 'react-router-dom';
import MuiTable from './mui-components/muitabelscholarships';

// TODO remove, this demo shouldn't need to reset the theme.
const defaultTheme = createTheme();

export default function Scholarships() {
  const [selectedRow, setSelectedRow] = React.useState(null);
  const history = useHistory();

  const handleRowSelect = (rowId) => {
    setSelectedRow(rowId);
  };

  const handleContinue = () => {
    if (selectedRow !== null) {
      history.push(`/scholarship/${selectedRow}`);
    }
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      <Box sx={{ display: 'flex' }}>
        <CssBaseline />
        <Box style={{ width: '100%', height: '100vh' }}>
          <h1 style={{ textAlign: 'center' }}>Scholarships</h1>
          <Container style={{ display: 'flex', minWidth: '90%', alignItems: 'center', flexDirection: 'column' }}>
            <MuiTable
              endpoint="http://localhost:8000/getallscholarships/"
              selectableRows
              onRowSelect={handleRowSelect}
              selectedRow={selectedRow}
            />
            <Button variant="contained" color="primary" onClick={handleContinue} disabled={selectedRow === null}>
              Continue
            </Button>
          </Container>
        </Box>
      </Box>
    </ThemeProvider>
  );
}
