import * as React from 'react';
import { useEffect, useState } from 'react';
import { DataGrid, GridColDef } from '@mui/x-data-grid';
import { Container, CssBaseline, Typography, Box, Button, Card, CardContent } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Avatar from '@mui/material/Avatar';
import SchoolIcon from '@mui/icons-material/School';
import { Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';


const defaultTheme = createTheme({
  palette: {
    primary: {
      main: '#ab0520', // primary color
    },
    secondary: {
      main: '#0c234b', // secondary color
    },
  },
});

interface Scholarship {
  id: number;
  scholarshipID: number;
  applications: number[];
  awardedApplications: number[];
  awardAmount: number;
  sponsorID: string;
  numberAvailable: number;
  majors: string;
  minors: string;
  gpa: number;
  deadline: Date;
  otherRequirements: string;
  scholarshipName: string;
}

const columns: GridColDef[] = [
  { field: 'scholarshipName', headerName: 'Scholarship Name', width: 200 },
  { field: 'awardAmount', headerName: 'Award Amount', width: 150 },
  { field: 'numberAvailable', headerName: 'Number Available', width: 180 },
  { field: 'deadline', headerName: 'Deadline', width: 150 },
];

export default function Scholarships() {
  const [rows, setRows] = useState<Scholarship[]>([]);
  const [selectedScholarships, setSelectedScholarships] = useState<Scholarship[]>([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('http://localhost:8000/getallscholarships/');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        // Assuming the data structure is { scholarships: Scholarship[] }
        if (data && data.scholarships) {
          setRows(data.scholarships);
        } else {
          console.error('Invalid data structure in API response:', data);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }
    fetchData();
  }, []);

  const handleSelectionChange = (selection: any) => {
    setSelectedScholarships(selection.rows.map((row: any) => row.data));
  };

  const [openDialog, setOpenDialog] = useState(false);

  const handleViewSelected = () => {
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };


  return (
    <div style={{
      backgroundImage: 'url(/UofA1.jpeg)',
      backgroundSize: 'cover',
      height: '100vh',
    }}>
      <ThemeProvider theme={defaultTheme}>
        <CssBaseline />
        <Container component="main" maxWidth="lg">
          <Box
            sx={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <SchoolIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Scholarships
            </Typography>
            <div style={{ height: 400, width: '100%', backgroundColor: '#ffffff' }}>
              <DataGrid
                rows={rows}
                columns={columns}
                checkboxSelection
                onRowSelectionModelChange={handleSelectionChange}
              />
            </div>
            {selectedScholarships.length > 0 && (
              <Button variant="contained" onClick={handleViewSelected}>
                View Selected Scholarships
              </Button>
            )}
            {selectedScholarships.length > 0 && (
              <Box mt={2}>
                <Typography variant="h6">Selected Scholarships:</Typography>
                {selectedScholarships.map((scholarship) => (
                  <Card key={scholarship.id} variant="outlined" style={{ margin: '8px', minWidth: '300px' }}>
                    <CardContent>
                      <Typography variant="subtitle1">{scholarship.scholarshipName}</Typography>
                      <Typography variant="body2">Award Amount: {scholarship.awardAmount}</Typography>
                      <Typography variant="body2">Number Available: {scholarship.numberAvailable}</Typography>
                      <Typography variant="body2">Deadline: {scholarship.deadline}</Typography>
                    </CardContent>
                  </Card>
                ))}
              </Box>
            )}
          </Box>
        </Container>
      </ThemeProvider>

      <Dialog open={openDialog} onClose={handleCloseDialog}>
        <DialogTitle>Selected Scholarships</DialogTitle>
        <DialogContent>
          {selectedScholarships.map((scholarship) => (
            <Card key={scholarship.id} variant="outlined" style={{ margin: '8px', minWidth: '300px' }}>
              <CardContent>
                <Typography variant="subtitle1">{scholarship.scholarshipName}</Typography>
                <Typography variant="body2">Award Amount: {scholarship.awardAmount}</Typography>
                <Typography variant="body2">Number Available: {scholarship.numberAvailable}</Typography>
                <Typography variant="body2">Deadline: {scholarship.deadline}</Typography>
              </CardContent>
            </Card>
          ))}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog} color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>



    </div>

  );
}
