import * as React from 'react';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import MuiTable from './mui-components/muitabelusers';    
import { useState } from 'react';

const defaultTheme = createTheme();

interface Scholarship {
    scholarshipID: string;
    applications: number;
    awardedApplications: number;
    awardAmount: number;
    sponsorID: string;
    numberAvailable: number;
    majors: string[];
    minors: string[];
    gpa: number;
    deadline: string;
    otherRequirements: string;
}

interface ViewScholarshipProps {
    scholarshipId: string;
}

export default function ViewScholarship(props: ViewScholarshipProps) {
    const [scholarship, setScholarship] = useState<Scholarship | null>(null);

    React.useEffect(() => {
        fetch(`http://localhost:8000/scholarship/${props.scholarshipId}`)
            .then(res => res.json())
            .then(data => setScholarship(data));
    }, [props.scholarshipId]);

    if (!scholarship) {
        return <div>Loading...</div>;
    }


    return (
        <ThemeProvider theme={defaultTheme}>
            <Box sx={{ display: 'flex' }}>
                <CssBaseline />
                <Box style={{ width: '100%', height: '100vh' }}>
                    <h1 style={{ textAlign: 'center' }}>Scholarship Details</h1>
                    <Container style={{ display: "flex", minWidth: "90%", alignItems: "center" }}>
                        <MuiTable endpoint={`http://localhost:8000/scholarship/${props.scholarshipId}`} />
                    </Container>
                </Box>
            </Box>
        </ThemeProvider>
    );
};
