import * as React from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Toolbar from '@mui/material/Toolbar';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';

export default function Application() {
    const [firstName, setFirstName] = React.useState('');
    const [lastName, setLastName] = React.useState('');
    const [essay, setEssay] = React.useState('');
    const [transcript, setTranscript] = React.useState('');
    const [recommendationLetter, setRecommendationLetter] = React.useState('');

    const handleSubmit = async (event: React.FormEvent) => {
        event.preventDefault();
        try {
            const response = await fetch('http://localhost:8000/createapplication/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    firstName,
                    lastName,
                    essay,
                    transcript,
                    recommendationLetter,
                }),
            });

            if (response.ok) {
                const data = await response.json();
                console.log('Form submitted successfully:', data);
                // You might want to update your component's state here, or redirect the user to another page
            } else {
                const errorData = await response.json();
                console.error('Form submission error:', errorData);
                // You might want to update your component's state here to show an error message
            }
        } catch (error) {
            console.error('Network error:', error);
            // You might want to update your component's state here to show an error message
        }
    };

    return (
        <React.Fragment>
            <CssBaseline />
            <AppBar position="static">
                <Toolbar>
                    <Typography variant="h6" color="inherit" noWrap>
                        Scholarship Application
                    </Typography>
                </Toolbar>
            </AppBar>
            <Container component="main" maxWidth="sm" sx={{ mt: 4 }}>
                <Paper variant="outlined" sx={{ p: 4 }}>
                    <Typography component="h1" variant="h4" align="center" sx={{ mb: 4 }}>
                        Apply for Scholarship
                    </Typography>
                    <form onSubmit={handleSubmit}>
                        <TextField
                            required
                            label="First Name"
                            fullWidth
                            value={firstName}
                            onChange={(event) => setFirstName(event.target.value)}
                            sx={{ mb: 2 }}
                        />
                        <TextField
                            required
                            label="Last Name"
                            fullWidth
                            value={lastName}
                            onChange={(event) => setLastName(event.target.value)}
                            sx={{ mb: 2 }}
                        />
                        <TextField
                            required
                            label="Essay"
                            fullWidth
                            multiline
                            rows={4}
                            value={essay}
                            onChange={(event) => setEssay(event.target.value)}
                            sx={{ mb: 2 }}
                        />
                        <TextField
                            required
                            label="Transcript"
                            fullWidth
                            multiline
                            rows={4}
                            value={transcript}
                            onChange={(event) => setTranscript(event.target.value)}
                            sx={{ mb: 2 }}
                        />
                        <TextField
                            required
                            label="Recommendation Letter"
                            fullWidth
                            multiline
                            rows={4}
                            value={recommendationLetter}
                            onChange={(event) => setRecommendationLetter(event.target.value)}
                            sx={{ mb: 2 }}
                        />
                        <Button type="submit" variant="contained" fullWidth>
                            Submit Application
                        </Button>
                    </form>
                </Paper>
            </Container>
        </React.Fragment>
    );
}
