import React from 'react';
import axios from 'axios';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@mui/material';


const AppMatch: React.FC = () => {
    const [scholarships, setScholarships] = React.useState([]);

    React.useEffect(() => {
        const gpa = sessionStorage.getItem('gpa');
        const majors = sessionStorage.getItem('majors');
        const minors = sessionStorage.getItem('minors');

        axios.post('http://localhost:8000/appmatch/', { gpa, majors, minors })
            .then(response => {
                setScholarships(response.data.scholarships);
            })
            .catch(error => {
                console.error('There was an error!', error);
            });
    }, []);
    return (
    <TableContainer>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Scholarship Name</TableCell>
            <TableCell>Award Amount</TableCell>
            <TableCell>Number Available</TableCell>
            <TableCell>Majors</TableCell>
            <TableCell>Minors</TableCell>
            <TableCell>GPA</TableCell>
            <TableCell>Deadline</TableCell>
            <TableCell>Other Requirements</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
            {scholarships.map((scholarship: any) => (

                <TableRow key={scholarship.scholarshipID}>
                    <TableCell>{scholarship.scholarshipName}</TableCell>
                    <TableCell>{scholarship.awardAmount}</TableCell>
                    <TableCell>{scholarship.numberAvailable}</TableCell>
                    <TableCell>{scholarship.majors}</TableCell>
                    <TableCell>{scholarship.minors}</TableCell>
                    <TableCell>{scholarship.gpa}</TableCell>
                    <TableCell>{scholarship.deadline}</TableCell>
                    <TableCell>{scholarship.otherRequirements}</TableCell>
                </TableRow>
            ))}
        </TableBody>
      </Table>
    </TableContainer>
    );
}

export default AppMatch;
